
public class Nestedloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1; i<10; i++) {
			for (int j=1; j<10; j++) {
				System.out.print(i+"*"+j+"="+i*j); // 1*1=1
				System.out.print('\t'); // ег
			}
		System.out.println();
		}
		
	}

}
